void merge_sort ( int low, int high ) 
{ 
 int mid; 

 if( low < high ) 
 { 
  mid = ( low + high ) / 2; 
  merge_sort ( low, mid ); 
  merge_sort ( mid+1, high ); 
  merge ( low, mid, high ); 
 } 
} 

void merge ( int low, int mid, int high ) 
{ 
 int h, i, j, b[50], k; 

 h = low; 
 i = low; 
 j = mid + 1; 

 while (( h<=mid ) && ( j<=high )) 
 { 
