  { 
   b[i] = a[h]; 
   h++; 
  } 

  else 
  { 
   b[i] = a[j]; 
   j++; 
  } 
  i++; 
 } 

 if ( h > mid ) 
 { 
  for ( k=j; k<=high; k++ ) 
  { 
   b[i] = a[k]; 
   i++; 
  } 
 } 

 else 
 { 
  for ( k=h; k<=mid; k++ ) 
  { 
