#include <iostream.h> 
#include <conio.h> 
class BCA 
{ 
 public: 
 int rollno; 
 float age; 
 char grade; 

 BCA (int rn, float ag, char g) 
 { 
  rollno = rn; 
  age = ag; 
  grade = g; 
 } 

 void operator ++ (int) 
 { 
  rollno = rollno + 1; 
  age = age + 1; 
  grade = grade + 1; 
 } 
}; 

void main() 
{ 
 clrscr(); 
 BCA obj(20, 5.0, 'A'); 
  cout<<"Before overloading the values of variables are: \n"; 
 cout<<obj.rollno<<endl<<obj.age<<endl<<obj.grade; 
 obj++; 

 cout<<"\nAfter overloading the values of variables are: \n"; 
 cout<<obj.rollno<<endl<<obj.age<<endl<<obj.grade; 
 getch(); 
}

